import{_ as t,c as a,o as i,ag as o}from"./chunks/framework.BI_O3LwE.js";const p=JSON.parse('{"title":"Quick Start Guide","description":"","frontmatter":{},"headers":[],"relativePath":"quick-start.md","filePath":"quick-start.md"}'),l={name:"quick-start.md"};function r(s,e,n,h,u,c){return i(),a("div",null,e[0]||(e[0]=[o('<h1 id="quick-start-guide" tabindex="-1">Quick Start Guide <a class="header-anchor" href="#quick-start-guide" aria-label="Permalink to &quot;Quick Start Guide&quot;">​</a></h1><h2 id="getting-started-with-textalchemy" tabindex="-1">Getting Started with TextAlchemy <a class="header-anchor" href="#getting-started-with-textalchemy" aria-label="Permalink to &quot;Getting Started with TextAlchemy&quot;">​</a></h2><p>TextAlchemy is designed to be simple and intuitive. Here&#39;s how to get started in just a few minutes:</p><h2 id="basic-usage" tabindex="-1">Basic Usage <a class="header-anchor" href="#basic-usage" aria-label="Permalink to &quot;Basic Usage&quot;">​</a></h2><h3 id="_1-open-the-popup" tabindex="-1">1. Open the Popup <a class="header-anchor" href="#_1-open-the-popup" aria-label="Permalink to &quot;1. Open the Popup&quot;">​</a></h3><ul><li>Click the TextAlchemy icon in your Chrome toolbar</li><li>The popup will open with a clean, modern interface</li></ul><h3 id="_2-type-your-text" tabindex="-1">2. Type Your Text <a class="header-anchor" href="#_2-type-your-text" aria-label="Permalink to &quot;2. Type Your Text&quot;">​</a></h3><ul><li>Enter any text in the input field (up to 500 characters)</li><li>You&#39;ll see real-time formatting in all available styles</li></ul><h3 id="_3-copy-your-favorite-styles" tabindex="-1">3. Copy Your Favorite Styles <a class="header-anchor" href="#_3-copy-your-favorite-styles" aria-label="Permalink to &quot;3. Copy Your Favorite Styles&quot;">​</a></h3><ul><li>Click the &quot;Copy&quot; button next to any style you like</li><li>The text is instantly copied to your clipboard</li><li>You&#39;ll see a &quot;Copied!&quot; confirmation</li></ul><h3 id="_4-use-your-formatted-text" tabindex="-1">4. Use Your Formatted Text <a class="header-anchor" href="#_4-use-your-formatted-text" aria-label="Permalink to &quot;4. Use Your Formatted Text&quot;">​</a></h3><ul><li>Paste the copied text anywhere - social media, messages, documents</li><li>The Unicode characters work across all platforms</li></ul><h2 id="advanced-features" tabindex="-1">Advanced Features <a class="header-anchor" href="#advanced-features" aria-label="Permalink to &quot;Advanced Features&quot;">​</a></h2><h3 id="show-more-styles" tabindex="-1">Show More Styles <a class="header-anchor" href="#show-more-styles" aria-label="Permalink to &quot;Show More Styles&quot;">​</a></h3><ul><li>Click &quot;Show More Styles&quot; to see additional formatting options</li><li>Click &quot;Show Less Styles&quot; to return to the top 10 styles</li></ul><h3 id="keyboard-shortcuts" tabindex="-1">Keyboard Shortcuts <a class="header-anchor" href="#keyboard-shortcuts" aria-label="Permalink to &quot;Keyboard Shortcuts&quot;">​</a></h3><ul><li>Press <code>Ctrl/Cmd + Shift + F</code> on any webpage</li><li>A floating widget appears for quick formatting</li><li>Perfect for when you&#39;re browsing and want to format text quickly</li></ul><h3 id="context-menu" tabindex="-1">Context Menu <a class="header-anchor" href="#context-menu" aria-label="Permalink to &quot;Context Menu&quot;">​</a></h3><ul><li>Select any text on a webpage</li><li>Right-click and choose &quot;TextAlchemy&quot;</li><li>Select a quick style or &quot;Open TextAlchemy&quot; for full options</li></ul><h2 id="popular-use-cases" tabindex="-1">Popular Use Cases <a class="header-anchor" href="#popular-use-cases" aria-label="Permalink to &quot;Popular Use Cases&quot;">​</a></h2><h3 id="social-media-posts" tabindex="-1">Social Media Posts <a class="header-anchor" href="#social-media-posts" aria-label="Permalink to &quot;Social Media Posts&quot;">​</a></h3><ul><li>Make your posts stand out with eye-catching text</li><li>Perfect for Twitter, Instagram, Facebook, and more</li></ul><h3 id="messages-and-chats" tabindex="-1">Messages and Chats <a class="header-anchor" href="#messages-and-chats" aria-label="Permalink to &quot;Messages and Chats&quot;">​</a></h3><ul><li>Add personality to your messages</li><li>Great for Discord, WhatsApp, Telegram, etc.</li></ul><h3 id="content-creation" tabindex="-1">Content Creation <a class="header-anchor" href="#content-creation" aria-label="Permalink to &quot;Content Creation&quot;">​</a></h3><ul><li>Create engaging titles and headings</li><li>Add visual appeal to your content</li></ul><h3 id="note-taking" tabindex="-1">Note Taking <a class="header-anchor" href="#note-taking" aria-label="Permalink to &quot;Note Taking&quot;">​</a></h3><ul><li>Make important notes more memorable</li><li>Organize information with different text styles</li></ul><h2 id="tips-and-tricks" tabindex="-1">Tips and Tricks <a class="header-anchor" href="#tips-and-tricks" aria-label="Permalink to &quot;Tips and Tricks&quot;">​</a></h2><h3 id="character-limit" tabindex="-1">Character Limit <a class="header-anchor" href="#character-limit" aria-label="Permalink to &quot;Character Limit&quot;">​</a></h3><ul><li>Each style has a 500-character limit</li><li>Longer text will be truncated</li></ul><h3 id="copy-multiple-styles" tabindex="-1">Copy Multiple Styles <a class="header-anchor" href="#copy-multiple-styles" aria-label="Permalink to &quot;Copy Multiple Styles&quot;">​</a></h3><ul><li>You can copy multiple styles for the same text</li><li>Each copy action replaces the previous one in your clipboard</li></ul><h3 id="browser-compatibility" tabindex="-1">Browser Compatibility <a class="header-anchor" href="#browser-compatibility" aria-label="Permalink to &quot;Browser Compatibility&quot;">​</a></h3><ul><li>Works in all modern browsers</li><li>Some Unicode characters may display differently on older systems</li></ul><h3 id="offline-usage" tabindex="-1">Offline Usage <a class="header-anchor" href="#offline-usage" aria-label="Permalink to &quot;Offline Usage&quot;">​</a></h3><ul><li>TextAlchemy works completely offline</li><li>No internet connection required</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="text-not-copying" tabindex="-1">Text Not Copying <a class="header-anchor" href="#text-not-copying" aria-label="Permalink to &quot;Text Not Copying&quot;">​</a></h3><ul><li>Make sure you&#39;re on an HTTPS site (for clipboard API)</li><li>Try clicking the copy button again</li><li>Check if another extension is interfering</li></ul><h3 id="styles-not-displaying" tabindex="-1">Styles Not Displaying <a class="header-anchor" href="#styles-not-displaying" aria-label="Permalink to &quot;Styles Not Displaying&quot;">​</a></h3><ul><li>Some Unicode characters may show as boxes (□) on older systems</li><li>This is normal and the text will still work when pasted</li></ul><h3 id="extension-not-working" tabindex="-1">Extension Not Working <a class="header-anchor" href="#extension-not-working" aria-label="Permalink to &quot;Extension Not Working&quot;">​</a></h3><ul><li>Try refreshing the page</li><li>Check if the extension is enabled in <code>chrome://extensions/</code></li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><p>Now that you know the basics, explore the <a href="/text-alchemy-chrome-extension/features.html">Features</a> page to see all available text styles, or check out the <a href="/text-alchemy-chrome-extension/usage.html">Usage Guide</a> for more advanced techniques!</p>',46)]))}const m=t(l,[["render",r]]);export{p as __pageData,m as default};
